import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { userActions } from '../_actions';

class Login extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.dispatch(userActions.logout());

        this.state = {
            Usuario: '',
            Contrasena: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { Usuario, Contrasena } = this.state;
        const { dispatch } = this.props;
        if (Usuario && Contrasena) {
            dispatch(userActions.login(Usuario, Contrasena));
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { Usuario, Contrasena, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !Usuario ? ' has-error' : '')}>
                        <label htmlFor="Usuario">Usuario</label>
                        <input type="text" className="form-control" name="Usuario" value={Usuario} onChange={this.handleChange} />
                        {submitted && !Usuario &&
                            <div className="help-block">Usuario es requiredo</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !Contrasena ? ' has-error' : '')}>
                        <label htmlFor="Contrasena">Contraseña</label>
                        <input type="Contrasena" className="form-control" name="Contrasena" value={Contrasena} onChange={this.handleChange} />
                        {submitted && !Contrasena &&
                            <div className="help-block">Contrasena es requireda</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <Link to="/registrar" className="btn btn-link">Registrar</Link>
                    </div>
                </form>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

const connectedLogin = connect(mapStateToProps)(Login);
export { connectedLogin as Login };
export default Login;